import React, { useState, useEffect } from 'react';
import Persons from '../persons/persons';
import Filter from '../filter/filter';
import PersonForm from '../person_form/person_form';
import personsService from '../../services/persons';

const App = () => {
  const [persons, setPersons] = useState([]);
  const [filteredPersons, setFilteredPersons] = useState([]);
  const [newName, setNewName] = useState('');
  const [newNumber, setNewNumber] = useState('');
  const [filter, setFilter] = useState('');

  useEffect(() => {
    personsService.getAll().then((res) => setPersons(res));
  }, []);

  useEffect(() => {
    setFilteredPersons(
      persons.filter((e) => e.name.toLowerCase().includes(filter.toLowerCase()))
    );
  }, [filter, persons]);

  const handleNewName = (event) => {
    setNewName(event.target.value);
  };

  const handleNewNumber = (event) => {
    setNewNumber(event.target.value);
  };

  const handleNewFilter = (event) => {
    setFilter(event.target.value);
  };

  const handleDeleteBut = (id, name) => {
    // if (window.confirm(`delete ${name}?`)) {
    personsService
      .del(id)
      .then(personsService.getAll().then((res) => setPersons(res)));
    // }
  };

  const handleAddBut = (event) => {
    event.preventDefault();
    if (persons.find((e) => e.name === newName)) {
      window.alert(`${newName} is already added to phonebook`);
    } else {
      personsService
        .create({ name: newName, number: newNumber })
        .then(personsService.getAll().then((res) => setPersons(res)).then);
    }
    document.getElementById().innerHTML = '';
  };

  return (
    <div>
      <h2>Phonebook</h2>
      <Filter handleNewFilter={handleNewFilter} />
      <h3>Add a new</h3>
      <PersonForm
        handleNewName={handleNewName}
        handleNewNumber={handleNewNumber}
        handleAddBut={handleAddBut}
      />
      <h3>Numbers</h3>
      {filteredPersons && (
        <Persons
          handleDeleteBut={handleDeleteBut}
          filteredPersons={filteredPersons}
        />
      )}
    </div>
  );
};

export default App;
