import React from 'react';

export default function Notification({ message }) {
  const errorStyle = {
    color: 'green',
    background: 'lightgrey',
    fontSize: 22,
    borderStyle: 'solid',
    borderRadius: 5,
    padding: 10,
    marginBottom: 10,
  };

  if (message === null) return null;

  return (
    <div style={errorStyle} className='error'>
      {message}
    </div>
  );
}
